import requests

api_key = 'YOUR_API_KEY'
query = '#AI OR #ArtificialIntelligence'
url = f'https://api.twitterapi.io/twitter/tweet/advanced_search?query={query}'

headers = {
    'x-api-key': f'{api_key}'
}

response = requests.get(url, headers=headers)

